SELECT * FROM student AS s INNER JOIN mark AS m ON s.id = m.student_id GROUP BY s.id HAVING AVG(m.mark) >8;
SELECT s.id, s.name FROM student s INNER JOIN mark AS m ON s.id = m.student.id GROUP BY s.id HAVING MIN(m.mark)>7;
SELECT s.id, s.name FROM student s INNER JOIN payment AS p ON s.id= p.student_id WHERE p.payment_date BETWEEN '2019-01-01' AND '2019-12-31' GROUP BY s.id HAVING COUNT(p.amount)>2;